(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["pages-story-ya"],{"217a":function(t,n,a){"use strict";var e=a("6ab9"),r=a.n(e);r.a},"45be":function(t,n,a){"use strict";a.r(n);var e=a("4968"),r=a("5b0a");for(var i in r)"default"!==i&&function(t){a.d(n,t,(function(){return r[t]}))}(i);a("217a");var u,c=a("f0c5"),f=Object(c["a"])(r["default"],e["b"],e["c"],!1,null,"b72c94ee",null,!1,e["a"],u);n["default"]=f.exports},4968:function(t,n,a){"use strict";var e;a.d(n,"b",(function(){return r})),a.d(n,"c",(function(){return i})),a.d(n,"a",(function(){return e}));var r=function(){var t=this,n=t.$createElement,a=t._self._c||n;return a("v-uni-view",[a("div",[a("div",{staticClass:"title"},[t._v("盐水鸭——江苏省南京市")]),a("div",{staticClass:"article"},[t._v("宋代，南京城盛行用鸭配菜，并有“无鸭不成席”之说。明代初年，南京流传一首民谣“古书院，琉璃塔，玄色缎子，咸板鸭”。同时，金陵烤鸭也已闻名遐迩，成为明代宫中宴席上不可缺少的名菜。明代，吏部左侍郎顾元起所著《客座赘语》中写道：“购觅取肥者，用微暖老汁浸润之，火炙色极嫩，秋冬尤妙，俗称为板鸭，其汁陈数十年者，且有子孙收贮，以为恒业，每一锅有值百余金，洵江宁特品也。”\n清代美食家袁牧，在其所著《随园食单》中有板鸭、挂炉烤鸭的制作方法介绍及326种名菜名点。清代南京方志学家陈作霖《金陵琐志》载称：“鸭非金陵所产也，率于邵伯、高邮间取之。么凫稚鹜千百成群，渡江而南，阑池塘以畜之。约以十旬肥美可食。……而皆不及‘盐水鸭’之为无上品也，淡而旨，肥而不浓。”盐水鸭一年四季均可制作，尤以农历八月至九月底，稻谷飘香、桂花盛开时制作者为上品，习呼为桂花鸭，以其“肉内有桂花香也”。\n民国政府定都南京后，进一步刺激了鸭馔业的发展。除大小菜馆烹制鸭馔外，还形成了鸭铺行业，最高时全市有156家，鸭馔的年销量高达51万只，出现了濮恒兴、刘天兴、马恒兴、韩复兴等新的盐水鸭八大家。特别是马恒兴菜馆的鸭馔美人肝和韩复兴板鸭店的板鸭尤为出名。《白门食谱》载：金陵八月时期，盐水鸭最著名，人人以为肉内有桂花香也。”《冶城话旧》云：“南京以善制鸭著，盐水鸭、板鸭、酱鸭，名目繁多。沙湾濮恒兴，武定桥刘天兴，皆各鸭子铺，然予犹取宴乐兴，买一鸭可以成全席。")])])])},i=[]},5684:function(t,n,a){var e=a("24fb");n=e(!1),n.push([t.i,".title[data-v-b72c94ee]{font-size:10px}",""]),t.exports=n},"5b0a":function(t,n,a){"use strict";a.r(n);var e=a("aa2f"),r=a.n(e);for(var i in e)"default"!==i&&function(t){a.d(n,t,(function(){return e[t]}))}(i);n["default"]=r.a},"6ab9":function(t,n,a){var e=a("5684");"string"===typeof e&&(e=[[t.i,e,""]]),e.locals&&(t.exports=e.locals);var r=a("4f06").default;r("2f66d495",e,!0,{sourceMap:!1,shadowMode:!1})},aa2f:function(t,n){}}]);